// AI-Powered Anomaly Detection Engine
const items = $input.all();
const results = [];

// Historical baseline data (in production, this would be stored in database)
const baselines = {
  cpu_usage: { mean: 45, stdDev: 15, threshold: 2.5 },
  memory_usage: { mean: 60, stdDev: 10, threshold: 2.0 },
  disk_usage: { mean: 30, stdDev: 8, threshold: 2.0 },
  response_time: { mean: 150, stdDev: 50, threshold: 2.0 },
  error_rate: { mean: 0.5, stdDev: 0.2, threshold: 3.0 }
};

// Z-Score Anomaly Detection Function
function detectAnomalies(value, metric) {
  const baseline = baselines[metric];
  if (!baseline) return { isAnomaly: false, severity: 'normal' };

  const zScore = Math.abs((value - baseline.mean) / baseline.stdDev);

  if (zScore > baseline.threshold) {
    const severity = zScore > baseline.threshold * 1.5 ? 'critical' : 
                    zScore > baseline.threshold * 1.2 ? 'high' : 'medium';
    return { 
      isAnomaly: true, 
      severity, 
      zScore: zScore.toFixed(2),
      threshold: baseline.threshold
    };
  }
  return { isAnomaly: false, severity: 'normal', zScore: zScore.toFixed(2) };
}

// Pattern Recognition for Log Analysis
function analyzeLogPatterns(logs) {
  const patterns = {
    database_errors: /database|connection|timeout|deadlock/i,
    memory_issues: /out of memory|oom|heap|garbage/i,
    authentication_failures: /auth|login|unauthorized|forbidden/i,
    performance_degradation: /slow|timeout|latency|performance/i
  };

  const detectedPatterns = [];

  logs.forEach(log => {
    Object.entries(patterns).forEach(([pattern, regex]) => {
      if (regex.test(log.message)) {
        detectedPatterns.push({
          type: pattern,
          severity: log.level === 'ERROR' ? 'high' : 'medium',
          timestamp: log.timestamp,
          service: log.service,
          message: log.message
        });
      }
    });
  });

  return detectedPatterns;
}

// Process each monitoring data source
for (const item of items) {
  let anomalies = [];
  let incidents = [];

  // Analyze metrics data
  if (item.json.source === 'metrics') {
    const metrics = item.json.data;
    Object.entries(metrics).forEach(([metric, value]) => {
      const anomaly = detectAnomalies(value, metric);
      if (anomaly.isAnomaly) {
        anomalies.push({
          type: 'metric_anomaly',
          metric: metric,
          value: value,
          severity: anomaly.severity,
          zScore: anomaly.zScore,
          timestamp: new Date().toISOString()
        });
      }
    });
  }

  // Analyze log patterns
  if (item.json.source === 'logs') {
    const logPatterns = analyzeLogPatterns(item.json.logs || []);
    anomalies.push(...logPatterns);
  }

  // Analyze endpoint health
  if (item.json.source === 'endpoints') {
    const endpoints = item.json.endpoints || [];
    endpoints.forEach(endpoint => {
      if (endpoint.status !== 200 || endpoint.responseTime > 5000) {
        anomalies.push({
          type: 'endpoint_failure',
          endpoint: endpoint.url,
          status: endpoint.status,
          responseTime: endpoint.responseTime,
          severity: endpoint.status >= 500 ? 'critical' : 'high',
          timestamp: new Date().toISOString()
        });
      }
    });
  }

  // Intelligent Incident Classification
  if (anomalies.length > 0) {
    // Group related anomalies into incidents
    const incidentGroups = {};

    anomalies.forEach(anomaly => {
      let incidentKey = 'general';

      // Classify incident types based on anomaly patterns
      if (anomaly.type === 'database_errors' || anomaly.metric === 'database_connections') {
        incidentKey = 'database_incident';
      } else if (anomaly.type === 'memory_issues' || anomaly.metric === 'memory_usage') {
        incidentKey = 'memory_incident';
      } else if (anomaly.type === 'endpoint_failure') {
        incidentKey = 'service_availability_incident';
      } else if (anomaly.type === 'performance_degradation') {
        incidentKey = 'performance_incident';
      }

      if (!incidentGroups[incidentKey]) {
        incidentGroups[incidentKey] = {
          id: `INC-${Date.now()}-${Math.random().toString(36).substr(2, 9)}`,
          type: incidentKey,
          severity: 'low',
          anomalies: [],
          created: new Date().toISOString(),
          status: 'detected'
        };
      }

      incidentGroups[incidentKey].anomalies.push(anomaly);

      // Escalate severity based on anomaly severity
      const severityLevels = { low: 1, medium: 2, high: 3, critical: 4 };
      const currentLevel = severityLevels[incidentGroups[incidentKey].severity] || 1;
      const anomalyLevel = severityLevels[anomaly.severity] || 1;

      if (anomalyLevel > currentLevel) {
        incidentGroups[incidentKey].severity = anomaly.severity;
      }
    });

    incidents = Object.values(incidentGroups);
  }

  results.push({
    json: {
      timestamp: new Date().toISOString(),
      source: item.json.source,
      anomalies_detected: anomalies.length,
      incidents_created: incidents.length,
      anomalies: anomalies,
      incidents: incidents,
      ai_analysis: {
        detection_algorithm: 'z-score_anomaly_detection',
        pattern_recognition: 'regex_based_log_analysis',
        classification_method: 'rule_based_incident_grouping',
        confidence_score: anomalies.length > 0 ? 0.85 : 0.95
      }
    }
  });
}

return results;

// Intelligent Self-Healing Automation Engine
const items = $input.all();
const remediationResults = [];

// Self-healing action definitions
const healingActions = {
  database_incident: {
    actions: [
      {
        name: 'restart_database_connections',
        command: 'systemctl restart postgresql',
        timeout: 30000,
        success_criteria: 'connection_count > 0'
      },
      {
        name: 'clear_connection_pool',
        command: 'redis-cli FLUSHDB',
        timeout: 10000,
        success_criteria: 'pool_size < 50'
      }
    ]
  },
  memory_incident: {
    actions: [
      {
        name: 'restart_memory_intensive_services',
        command: 'systemctl restart apache2 nginx',
        timeout: 45000,
        success_criteria: 'memory_usage < 80'
      },
      {
        name: 'clear_system_cache',
        command: 'echo 3 > /proc/sys/vm/drop_caches',
        timeout: 5000,
        success_criteria: 'cache_cleared = true'
      }
    ]
  },
  service_availability_incident: {
    actions: [
      {
        name: 'restart_failed_services',
        command: 'systemctl restart nginx apache2',
        timeout: 30000,
        success_criteria: 'service_status = active'
      },
      {
        name: 'update_load_balancer',
        command: 'nginx -s reload',
        timeout: 15000,
        success_criteria: 'lb_config_updated = true'
      }
    ]
  }
};

// Remediation execution simulator (in production, this would execute actual commands)
function executeRemediation(incident) {
  const healingPlan = healingActions[incident.type];

  if (!healingPlan) {
    return {
      incident_id: incident.id,
      status: 'no_auto_remediation',
      message: 'No automated remediation available for this incident type'
    };
  }

  const results = [];

  healingPlan.actions.forEach(action => {
    // Simulate execution (replace with actual command execution in production)
    const success = Math.random() > 0.2; // 80% success rate simulation

    results.push({
      action: action.name,
      command: action.command,
      status: success ? 'completed' : 'failed',
      execution_time: Math.floor(Math.random() * action.timeout),
      timestamp: new Date().toISOString()
    });
  });

  const allSuccessful = results.every(r => r.status === 'completed');

  return {
    incident_id: incident.id,
    incident_type: incident.type,
    status: allSuccessful ? 'resolved' : 'partial_remediation',
    actions_executed: results.length,
    successful_actions: results.filter(r => r.status === 'completed').length,
    execution_details: results,
    resolution_time: Math.max(...results.map(r => r.execution_time)),
    ai_confidence: allSuccessful ? 0.95 : 0.7
  };
}

// Process incidents for auto-remediation
for (const item of items) {
  const incidents = item.json.incidents || [];

  incidents.forEach(incident => {
    // Only attempt auto-remediation for specific incident types
    if (['database_incident', 'memory_incident', 'service_availability_incident'].includes(incident.type)) {
      const remediationResult = executeRemediation(incident);
      remediationResults.push(remediationResult);
    }
  });
}

return [{
  json: {
    timestamp: new Date().toISOString(),
    total_incidents_processed: remediationResults.length,
    successful_remediations: remediationResults.filter(r => r.status === 'resolved').length,
    partial_remediations: remediationResults.filter(r => r.status === 'partial_remediation').length,
    failed_remediations: remediationResults.filter(r => r.status === 'no_auto_remediation').length,
    remediation_details: remediationResults,
    ai_healing_stats: {
      average_resolution_time: remediationResults.length > 0 ? 
        remediationResults.reduce((sum, r) => sum + (r.resolution_time || 0), 0) / remediationResults.length : 0,
      success_rate: remediationResults.length > 0 ? 
        (remediationResults.filter(r => r.status === 'resolved').length / remediationResults.length * 100).toFixed(2) : 0
    }
  }
}];
