// Advanced Customer Intelligence Engine with ML-driven Insights
const items = $input.all();
const processedCustomers = [];
const engagementModels = {};
const channelPreferences = {};
const behavioralSegments = {};

// Machine Learning Models Configuration
const mlModels = {
  engagement_prediction: {
    weights: {
      email_open_rate: 0.25,
      click_through_rate: 0.30,
      website_session_duration: 0.20,
      purchase_frequency: 0.35,
      social_engagement: 0.15,
      content_consumption: 0.20
    },
    features: ['recency', 'frequency', 'monetary', 'engagement_score', 'channel_preference']
  },
  churn_prediction: {
    risk_factors: {
      declining_engagement: 0.40,
      support_tickets: 0.25,
      feature_usage_drop: 0.30,
      payment_issues: 0.35
    },
    time_windows: [7, 14, 30, 90] // days
  },
  optimal_timing: {
    channels: {
      email: { peak_hours: [9, 11, 14, 16], peak_days: [1, 2, 3, 4] },
      sms: { peak_hours: [10, 12, 15, 18], peak_days: [1, 2, 3, 4, 5] },
      push: { peak_hours: [8, 12, 17, 20], peak_days: [0, 1, 2, 3, 4, 5, 6] },
      social: { peak_hours: [7, 12, 17, 21], peak_days: [0, 1, 2, 3, 4, 5, 6] }
    }
  }
};

// Customer Journey Stages with AI-driven progression logic
const journeyStages = {
  awareness: { weight: 1.0, next_stages: ['consideration'], typical_duration: 7 },
  consideration: { weight: 1.5, next_stages: ['evaluation', 'trial'], typical_duration: 14 },
  evaluation: { weight: 2.0, next_stages: ['trial', 'purchase'], typical_duration: 21 },
  trial: { weight: 2.5, next_stages: ['purchase', 'churn'], typical_duration: 14 },
  purchase: { weight: 3.0, next_stages: ['onboarding', 'advocacy'], typical_duration: 1 },
  onboarding: { weight: 2.8, next_stages: ['retention', 'expansion'], typical_duration: 30 },
  retention: { weight: 3.2, next_stages: ['expansion', 'advocacy', 'churn'], typical_duration: 90 },
  expansion: { weight: 4.0, next_stages: ['advocacy', 'retention'], typical_duration: 45 },
  advocacy: { weight: 5.0, next_stages: ['retention', 'expansion'], typical_duration: 365 },
  churn: { weight: 0.1, next_stages: ['reactivation'], typical_duration: 180 },
  reactivation: { weight: 1.2, next_stages: ['consideration', 'churn'], typical_duration: 30 }
};

function calculateEngagementScore(customerData) {
  const model = mlModels.engagement_prediction;
  let score = 0;

  // Recency score (last interaction)
  const lastInteraction = new Date(customerData.last_interaction || customerData.created_at);
  const daysSinceInteraction = (Date.now() - lastInteraction.getTime()) / (1000 * 60 * 60 * 24);
  const recencyScore = Math.max(0, 1 - (daysSinceInteraction / 30));

  // Frequency score (interaction frequency)
  const interactionCount = customerData.interaction_count || 1;
  const accountAge = (Date.now() - new Date(customerData.created_at).getTime()) / (1000 * 60 * 60 * 24);
  const frequencyScore = Math.min(1, interactionCount / Math.max(1, accountAge / 7));

  // Monetary score (purchase behavior)
  const totalSpent = customerData.total_spent || 0;
  const avgOrderValue = customerData.avg_order_value || 0;
  const monetaryScore = Math.min(1, (totalSpent / 1000) + (avgOrderValue / 100));

  // Channel engagement scores
  const emailScore = (customerData.email_open_rate || 0) * model.weights.email_open_rate;
  const clickScore = (customerData.click_through_rate || 0) * model.weights.click_through_rate;
  const sessionScore = Math.min(1, (customerData.avg_session_duration || 0) / 300) * model.weights.website_session_duration;
  const purchaseScore = Math.min(1, (customerData.purchase_frequency || 0) / 12) * model.weights.purchase_frequency;
  const socialScore = Math.min(1, (customerData.social_engagement_score || 0) / 100) * model.weights.social_engagement;
  const contentScore = Math.min(1, (customerData.content_engagement_score || 0) / 100) * model.weights.content_consumption;

  score = (recencyScore + frequencyScore + monetaryScore + emailScore + clickScore + sessionScore + purchaseScore + socialScore + contentScore) / 3;

  return Math.min(1, Math.max(0, score));
}

function predictChurnRisk(customerData) {
  const model = mlModels.churn_prediction;
  let riskScore = 0;

  // Declining engagement analysis
  const currentEngagement = calculateEngagementScore(customerData);
  const historicalEngagement = customerData.historical_engagement_avg || currentEngagement;
  const engagementDecline = Math.max(0, historicalEngagement - currentEngagement);
  riskScore += engagementDecline * model.risk_factors.declining_engagement;

  // Support ticket analysis
  const recentTickets = customerData.support_tickets_30d || 0;
  const ticketRisk = Math.min(1, recentTickets / 5);
  riskScore += ticketRisk * model.risk_factors.support_tickets;

  // Feature usage analysis
  const currentUsage = customerData.feature_usage_score || 0.5;
  const previousUsage = customerData.previous_usage_score || currentUsage;
  const usageDrop = Math.max(0, previousUsage - currentUsage);
  riskScore += usageDrop * model.risk_factors.feature_usage_drop;

  // Payment issues analysis
  const paymentIssues = customerData.payment_failures_90d || 0;
  const paymentRisk = Math.min(1, paymentIssues / 3);
  riskScore += paymentRisk * model.risk_factors.payment_issues;

  return Math.min(1, riskScore);
}

function optimizeChannelTiming(customerData, timezone = 'UTC') {
  const timingModel = mlModels.optimal_timing;
  const personalizedTiming = {};

  // Analyze historical engagement patterns
  const historicalPatterns = customerData.engagement_patterns || {};

  Object.keys(timingModel.channels).forEach(channel => {
    const channelData = timingModel.channels[channel];
    const personalPattern = historicalPatterns[channel] || {};

    // Combine global patterns with personal preferences
    const optimalHours = personalPattern.peak_hours || channelData.peak_hours;
    const optimalDays = personalPattern.peak_days || channelData.peak_days;

    // Calculate next optimal send time
    const now = new Date();
    const nextOptimalTime = calculateNextOptimalTime(now, optimalHours, optimalDays, timezone);

    personalizedTiming[channel] = {
      next_optimal_time: nextOptimalTime,
      confidence_score: personalPattern.peak_hours ? 0.9 : 0.6,
      fallback_hours: channelData.peak_hours,
      recommended_frequency: calculateOptimalFrequency(customerData, channel)
    };
  });

  return personalizedTiming;
}

function calculateNextOptimalTime(currentTime, optimalHours, optimalDays, timezone) {
  const now = new Date(currentTime);
  const currentHour = now.getHours();
  const currentDay = now.getDay();

  // Find next optimal hour today
  const todayOptimalHours = optimalHours.filter(hour => hour > currentHour);

  if (todayOptimalHours.length > 0 && optimalDays.includes(currentDay)) {
    const nextHour = todayOptimalHours[0];
    const nextTime = new Date(now);
    nextTime.setHours(nextHour, 0, 0, 0);
    return nextTime.toISOString();
  }

  // Find next optimal day
  for (let dayOffset = 1; dayOffset <= 7; dayOffset++) {
    const checkDate = new Date(now);
    checkDate.setDate(now.getDate() + dayOffset);
    const checkDay = checkDate.getDay();

    if (optimalDays.includes(checkDay)) {
      const nextTime = new Date(checkDate);
      nextTime.setHours(optimalHours[0], 0, 0, 0);
      return nextTime.toISOString();
    }
  }

  // Fallback to tomorrow at first optimal hour
  const fallbackTime = new Date(now);
  fallbackTime.setDate(now.getDate() + 1);
  fallbackTime.setHours(optimalHours[0], 0, 0, 0);
  return fallbackTime.toISOString();
}

function calculateOptimalFrequency(customerData, channel) {
  const engagementScore = calculateEngagementScore(customerData);
  const baseFrequencies = {
    email: { min: 1, max: 14, optimal: 7 },
    sms: { min: 3, max: 30, optimal: 14 },
    push: { min: 1, max: 7, optimal: 3 },
    social: { min: 1, max: 3, optimal: 1 }
  };

  const channelFreq = baseFrequencies[channel] || baseFrequencies.email;

  // Adjust frequency based on engagement
  if (engagementScore > 0.8) {
    return Math.max(channelFreq.min, channelFreq.optimal * 0.7); // More frequent for highly engaged
  } else if (engagementScore > 0.5) {
    return channelFreq.optimal; // Standard frequency
  } else {
    return Math.min(channelFreq.max, channelFreq.optimal * 1.5); // Less frequent for low engagement
  }
}

function determineJourneyStage(customerData) {
  const signals = {
    website_visits: customerData.website_visits || 0,
    email_engagement: customerData.email_engagement_score || 0,
    content_downloads: customerData.content_downloads || 0,
    demo_requests: customerData.demo_requests || 0,
    trial_signup: customerData.trial_signup || false,
    purchases: customerData.purchase_count || 0,
    support_usage: customerData.support_usage || 0,
    referrals_made: customerData.referrals_made || 0,
    account_age_days: (Date.now() - new Date(customerData.created_at).getTime()) / (1000 * 60 * 60 * 24)
  };

  // Journey stage determination logic
  if (signals.referrals_made > 0 || (signals.purchases > 3 && signals.support_usage > 10)) {
    return 'advocacy';
  } else if (signals.purchases > 1 && signals.account_age_days > 90) {
    return 'expansion';
  } else if (signals.purchases > 0) {
    return signals.account_age_days < 30 ? 'onboarding' : 'retention';
  } else if (signals.trial_signup) {
    return 'trial';
  } else if (signals.demo_requests > 0 || signals.content_downloads > 2) {
    return 'evaluation';
  } else if (signals.website_visits > 1 || signals.email_engagement > 0.3) {
    return 'consideration';
  } else {
    return 'awareness';
  }
}

