  const variables = {
    customer_name: customerData.first_name || 'there',
    company_name: customerData.company || 'your company',
    industry_specific_value: getIndustryValue(customerData.industry),
    usage_stats: customerData.usage_stats || {},
    recent_activity: customerData.recent_activity || [],
    pain_points: identifyPainPoints(customerData),
    success_metrics: calculateSuccessMetrics(customerData),
    next_best_action: determineNextBestAction(customerData, journeyStage)
  };

  // Campaign-specific variables
  switch (campaignType) {
    case 'product_showcase':
      variables.relevant_features = getRelevantFeatures(customerData);
      variables.roi_projection = calculateROIProjection(customerData);
      break;
    case 'upgrade_recommendations':
      variables.current_plan = customerData.current_plan || 'basic';
      variables.usage_vs_limit = calculateUsageVsLimit(customerData);
      variables.upgrade_benefits = getUpgradeBenefits(customerData);
      break;
    case 'usage_insights':
      variables.top_features = getTopFeatures(customerData);
      variables.efficiency_gains = calculateEfficiencyGains(customerData);
      variables.benchmark_comparison = getBenchmarkComparison(customerData);
      break;
  }

  return variables;
}

function getIndustryValue(industry) {
  const industryValues = {
