    'healthcare': 'improve patient outcomes and operational efficiency',
    'finance': 'enhance security and regulatory compliance',
    'retail': 'optimize customer experience and inventory management',
    'manufacturing': 'streamline operations and reduce costs',
    'education': 'enhance learning outcomes and administrative efficiency'
  };

  return industryValues[industry?.toLowerCase()] || 'drive growth and efficiency';
}

function identifyPainPoints(customerData) {
  const painPoints = [];

  if ((customerData.support_tickets_30d || 0) > 2) {
    painPoints.push('frequent_support_needs');
  }

  if ((customerData.feature_adoption_rate || 0) < 0.3) {
    painPoints.push('low_feature_adoption');
  }

  if ((customerData.time_to_value || 30) > 14) {
    painPoints.push('slow_time_to_value');
  }

  return painPoints;
}

function calculateSuccessMetrics(customerData) {
  return {
    time_saved: (customerData.automation_minutes || 0) + ' minutes per week',
    efficiency_improvement: Math.round((customerData.efficiency_score || 1) * 100) + '%',
    cost_reduction: '$' + Math.round((customerData.cost_savings || 0)),
    productivity_boost: Math.round((customerData.productivity_increase || 0) * 100) + '%'
  };
}

function determineNextBestAction(customerData, journeyStage) {
  const actions = {
    awareness: 'explore our product tour',
    consideration: 'schedule a personalized demo',
    evaluation: 'start your free trial',
    trial: 'implement advanced features',
    purchase: 'complete your onboarding',
    onboarding: 'explore power-user features',
    retention: 'optimize your current usage',
    expansion: 'upgrade to unlock advanced capabilities',
    advocacy: 'join our referral program',
    churn: 'speak with our retention specialist',
    reactivation: 'discover what\'s new'
  };

  return actions[journeyStage] || 'continue your journey with us';
}

function getRelevantFeatures(customerData) {
  // This would typically use ML to determine relevant features based on similar customers
  const allFeatures = [
    'Advanced Analytics', 'Automated Workflows', 'API Integration',
    'Custom Dashboards', 'Real-time Collaboration', 'Mobile App',
    'Advanced Security', 'White-label Options', 'Priority Support'
  ];

  // Simple relevance scoring based on industry and current usage
  return allFeatures.slice(0, 3); // Return top 3 for simplicity
}

function calculateROIProjection(customerData) {
  const baseSavings = 1000; // Base monthly savings
  const industryMultiplier = customerData.industry === 'technology' ? 1.5 : 1.2;
  const companySizeMultiplier = (customerData.company_size || 50) / 50;

  const monthlySavings = baseSavings * industryMultiplier * companySizeMultiplier;
  const annualSavings = monthlySavings * 12;

  return {
    monthly: Math.round(monthlySavings),
    annual: Math.round(annualSavings),
    payback_period: '2.3 months'
  };
}

function calculateUsageVsLimit(customerData) {
  const currentUsage = customerData.current_usage || 0;
  const planLimit = customerData.plan_limit || 1000;
  const utilizationRate = currentUsage / planLimit;

  return {
    current_usage: currentUsage,
    plan_limit: planLimit,
    utilization_rate: Math.round(utilizationRate * 100),
    approaching_limit: utilizationRate > 0.8
  };
}

function getUpgradeBenefits(customerData) {
  const currentPlan = customerData.current_plan || 'basic';

  const upgradeBenefits = {
    basic: ['5x more API calls', 'Advanced analytics', 'Priority support'],
    standard: ['Unlimited API calls', 'Custom integrations', 'Dedicated success manager'],
    premium: ['Enterprise security', 'White-label options', 'SLA guarantee']
  };

  return upgradeBenefits[currentPlan] || upgradeBenefits.basic;
}

function getTopFeatures(customerData) {
  const usageData = customerData.feature_usage || {};

  return Object.entries(usageData)
    .sort(([,a], [,b]) => b - a)
    .slice(0, 3)
    .map(([feature, usage]) => ({ feature, usage }));
}

function calculateEfficiencyGains(customerData) {
  const baselineTime = customerData.baseline_time || 100;
  const currentTime = customerData.current_time || 70;
  const efficiency = ((baselineTime - currentTime) / baselineTime) * 100;

  return {
    time_saved_percentage: Math.round(efficiency),
    hours_saved_weekly: Math.round((baselineTime - currentTime) / 60 * 40), // 40 hours per week
    efficiency_score: Math.round(efficiency)
  };
}

function getBenchmarkComparison(customerData) {
  const userEngagement = calculateEngagementScore(customerData);
  const industryAvg = 0.6; // Industry average engagement

  return {
    user_score: Math.round(userEngagement * 100),
    industry_average: Math.round(industryAvg * 100),
    percentile: userEngagement > industryAvg ? 
      Math.round(((userEngagement - industryAvg) / (1 - industryAvg)) * 50 + 50) :
      Math.round((userEngagement / industryAvg) * 50)
  };
}

// Process each customer
items.forEach((item, index) => {
  const customerData = item.json;

  // Calculate core metrics
  const engagementScore = calculateEngagementScore(customerData);
  const churnRisk = predictChurnRisk(customerData);
  const journeyStage = determineJourneyStage(customerData);
  const channelTiming = optimizeChannelTiming(customerData, customerData.timezone);

  // Generate personalized campaigns
  const personalizedCampaigns = generatePersonalizedCampaigns(customerData, journeyStage, channelTiming);

  // Create customer intelligence profile
  const customerIntelligence = {
    customer_id: customerData.customer_id || customerData.id,
    profile: {
      ...customerData,
      engagement_score: engagementScore,
      churn_risk_score: churnRisk,
      journey_stage: journeyStage,
      predicted_ltv: calculatePredictedLTV(customerData, engagementScore),
      next_best_actions: determineNextBestActions(customerData, journeyStage, engagementScore)
    },
    channel_optimization: channelTiming,
    personalized_campaigns: personalizedCampaigns,
    ml_insights: {
      engagement_trend: analyzeEngagementTrend(customerData),
      channel_preferences: analyzeChannelPreferences(customerData),
      content_preferences: analyzeContentPreferences(customerData),
      optimal_frequency: calculateOptimalCampaignFrequency(customerData)
    },
    processing_metadata: {
      processed_at: new Date().toISOString(),
      model_version: '4.0',
      confidence_scores: {
        engagement_prediction: 0.92,
        churn_prediction: 0.88,
        channel_optimization: 0.85,
        campaign_personalization: 0.90
      }
    }
  };

  processedCustomers.push({ json: customerIntelligence });
});

function calculatePredictedLTV(customerData, engagementScore) {
  const baseValue = customerData.current_plan_value || 100;
  const engagementMultiplier = 1 + (engagementScore * 2);
  const retentionMultiplier = customerData.retention_probability || 0.8;
  const expansionMultiplier = customerData.expansion_probability || 1.2;

  return Math.round(baseValue * engagementMultiplier * retentionMultiplier * expansionMultiplier * 12);
}

function determineNextBestActions(customerData, journeyStage, engagementScore) {
  const actions = [];

  // Stage-specific actions
  if (journeyStage === 'trial' && engagementScore < 0.5) {
    actions.push({ action: 'intensive_onboarding', priority: 'high', timeline: '24h' });
  }

  if (journeyStage === 'retention' && engagementScore > 0.8) {
    actions.push({ action: 'expansion_outreach', priority: 'medium', timeline: '7d' });
  }

  // Universal actions based on engagement
  if (engagementScore < 0.3) {
    actions.push({ action: 'engagement_recovery', priority: 'high', timeline: '48h' });
  }

  return actions;
}

function analyzeEngagementTrend(customerData) {
  const recentEngagement = customerData.engagement_last_30d || [];

  if (recentEngagement.length < 2) {
    return { trend: 'insufficient_data', direction: 'stable', confidence: 0.1 };
  }

  const recentAvg = recentEngagement.slice(-7).reduce((a, b) => a + b, 0) / 7;
  const olderAvg = recentEngagement.slice(-30, -7).reduce((a, b) => a + b, 0) / 23;

  const change = (recentAvg - olderAvg) / olderAvg;

  let trend, direction;
  if (Math.abs(change) < 0.1) {
    trend = 'stable';
    direction = 'neutral';
  } else if (change > 0) {
    trend = 'improving';
    direction = 'up';
  } else {
    trend = 'declining';
    direction = 'down';
  }

  return {
    trend,
    direction,
    change_percentage: Math.round(change * 100),
    confidence: Math.min(0.9, recentEngagement.length / 30)
  };
}

function analyzeChannelPreferences(customerData) {
  const channelEngagement = customerData.channel_engagement || {};

  const preferences = Object.entries(channelEngagement)
    .sort(([,a], [,b]) => b - a)
    .map(([channel, engagement]) => ({ channel, engagement, preference_score: engagement }));

  return {
    primary_channel: preferences[0]?.channel || 'email',
    secondary_channel: preferences[1]?.channel || 'push',
    channel_rankings: preferences,
    multi_channel_preference: preferences.filter(p => p.engagement > 0.5).length > 1
  };
}

function analyzeContentPreferences(customerData) {
  const contentEngagement = customerData.content_engagement || {};

  return {
    preferred_content_types: Object.entries(contentEngagement)
      .sort(([,a], [,b]) => b - a)
      .slice(0, 3)
      .map(([type, engagement]) => ({ type, engagement })),
    content_frequency_preference: customerData.preferred_frequency || 'weekly',
    content_length_preference: customerData.preferred_length || 'medium',
    personalization_level: customerData.personalization_preference || 'high'
  };
}

function calculateOptimalCampaignFrequency(customerData) {
  const engagementScore = calculateEngagementScore(customerData);
  const churnRisk = predictChurnRisk(customerData);

  // Base frequency (campaigns per week)
  let frequency = 2;

  // Adjust based on engagement
  if (engagementScore > 0.8) frequency += 1;
  else if (engagementScore < 0.3) frequency -= 0.5;

  // Adjust based on churn risk
  if (churnRisk > 0.7) frequency += 0.5; // More frequent for at-risk customers

  // Ensure reasonable bounds
  frequency = Math.max(0.5, Math.min(5, frequency));

  return {
    campaigns_per_week: frequency,
    days_between_campaigns: Math.round(7 / frequency),
    reasoning: `Based on engagement score (${Math.round(engagementScore * 100)}%) and churn risk (${Math.round(churnRisk * 100)}%)`
  };
}

return processedCustomers;

