function assignVariant(customerId, testName) {
  const test = activeTests[testName];
  if (!test) return null;

  // Use customer ID as seed for consistent assignment
  const hash = customerId.toString().split('').reduce((a, b) => {
    a = ((a << 5) - a) + b.charCodeAt(0);
    return a & a;
  }, 0);

  const normalizedHash = Math.abs(hash) / Math.pow(2, 31);

  let cumulativeWeight = 0;
  for (const variant of test.variants) {
    cumulativeWeight += variant.weight;
    if (normalizedHash <= cumulativeWeight) {
      return {
        test_name: testName,
        variant_id: variant.id,
        variant_data: variant,
        assignment_timestamp: new Date().toISOString()
      };
    }
  }

  // Fallback to control
  return {
    test_name: testName,
    variant_id: 'control',
    variant_data: test.variants[0],
    assignment_timestamp: new Date().toISOString()
  };
}

function calculateStatisticalSignificance(controlData, variantData, metric) {
  if (!controlData.length || !variantData.length) {
    return { significant: false, p_value: 1, confidence: 0 };
  }

  // Calculate means
  const controlMean = controlData.reduce((sum, val) => sum + val, 0) / controlData.length;
  const variantMean = variantData.reduce((sum, val) => sum + val, 0) / variantData.length;

  // Calculate standard deviations
  const controlVariance = controlData.reduce((sum, val) => sum + Math.pow(val - controlMean, 2), 0) / (controlData.length - 1);
  const variantVariance = variantData.reduce((sum, val) => sum + Math.pow(val - variantMean, 2), 0) / (variantData.length - 1);

  // Calculate standard error
  const standardError = Math.sqrt(controlVariance / controlData.length + variantVariance / variantData.length);

  // Calculate t-statistic
  const tStatistic = Math.abs(variantMean - controlMean) / standardError;

  // Degrees of freedom (simplified)
  const degreesOfFreedom = controlData.length + variantData.length - 2;

  // Simplified p-value calculation (for demonstration)
  const pValue = 2 * (1 - normalCDF(Math.abs(tStatistic)));

  const significant = pValue < (1 - testFramework.confidence_level);
  const confidence = (1 - pValue) * 100;

  return {
    significant,
    p_value: pValue,
    confidence: Math.min(99.9, confidence),
    effect_size: Math.abs(variantMean - controlMean) / controlMean,
    control_mean: controlMean,
    variant_mean: variantMean,
    sample_sizes: { control: controlData.length, variant: variantData.length }
  };
}

function normalCDF(x) {
  // Approximation of normal cumulative distribution function
  return 0.5 * (1 + erf(x / Math.sqrt(2)));
}

function erf(x) {
  // Approximation of error function
  const a1 =  0.254829592;
  const a2 = -0.284496736;
  const a3 =  1.421413741;
  const a4 = -1.453152027;
  const a5 =  1.061405429;
  const p  =  0.3275911;

  const sign = x >= 0 ? 1 : -1;
  x = Math.abs(x);

  const t = 1.0 / (1.0 + p * x);
  const y = 1.0 - (((((a5 * t + a4) * t) + a3) * t + a2) * t + a1) * t * Math.exp(-x * x);

  return sign * y;
}

function calculateRequiredSampleSize(currentEffectSize, power = 0.8, alpha = 0.05) {
  // Simplified sample size calculation
  const zAlpha = 1.96; // For alpha = 0.05
  const zBeta = 0.84;  // For power = 0.8

  const requiredSize = Math.ceil(
    (2 * Math.pow(zAlpha + zBeta, 2)) / Math.pow(currentEffectSize, 2)
  );

  return Math.max(testFramework.minimum_sample_size, requiredSize);
}

