  // Detect performance patterns
  const detectedPatterns = detectPerformancePatterns(mockHistoricalData);

  // Generate optimization recommendations
  const overallPerformance = {
    overall_performance_score: campaignPerformance.reduce((sum, p) => sum + (p.overall_performance_score || 0), 0) / Math.max(1, campaignPerformance.length),
    metric_performance: {}
  };

  const optimizationRecommendations = generateOptimizationRecommendations(overallPerformance, detectedPatterns);

  const analyticsResult = {
    ...campaignData,
    performance_analytics: {
      individual_campaign_performance: campaignPerformance,
      overall_performance_score: overallPerformance.overall_performance_score,
      performance_grade: getPerformanceGrade(overallPerformance.overall_performance_score),
      detected_patterns: detectedPatterns,
      optimization_recommendations: optimizationRecommendations,
      analysis_metadata: {
        analyzed_campaigns: campaignPerformance.length,
        historical_data_points: mockHistoricalData.length,
        patterns_detected: detectedPatterns.length,
        recommendations_generated: optimizationRecommendations.length,
        analysis_timestamp: new Date().toISOString(),
        model_version: '4.0'
      }
    },
    learning_insights: {
      performance_trends: analyzePerformanceTrends(mockHistoricalData),
      channel_optimization_opportunities: identifyChannelOptimizations(campaignPerformance),
      customer_segment_insights: analyzeCustomerSegmentPerformance(campaignData),
      predictive_recommendations: generatePredictiveRecommendations(detectedPatterns, campaignData)
    }
  };

  performanceAnalytics.push({ json: analyticsResult });
});

function analyzePerformanceTrends(historicalData) {
  const recentData = historicalData.slice(0, 30); // Last 30 data points
  const olderData = historicalData.slice(30, 60); // Previous 30 data points

  const recentAvg = recentData.reduce((sum, d) => sum + d.overall_performance_score, 0) / recentData.length;
  const olderAvg = olderData.length > 0 ? olderData.reduce((sum, d) => sum + d.overall_performance_score, 0) / olderData.length : recentAvg;

  const trend = recentAvg > olderAvg ? 'improving' : recentAvg < olderAvg ? 'declining' : 'stable';
  const changePercent = olderAvg > 0 ? ((recentAvg - olderAvg) / olderAvg * 100) : 0;

  return {
    trend_direction: trend,
    change_percentage: changePercent.toFixed(2),
    current_performance: recentAvg.toFixed(3),
    previous_performance: olderAvg.toFixed(3),
    confidence: Math.min(0.9, recentData.length / 30)
  };
}

function identifyChannelOptimizations(campaignPerformance) {
  const optimizations = [];

  campaignPerformance.forEach(performance => {
    Object.entries(performance.metric_performance || {}).forEach(([metric, data]) => {
      if (data.status === 'below_benchmark') {
        optimizations.push({
          channel: performance.channel,
          metric: metric,
          current_value: data.actual,
          benchmark_value: data.benchmark,
          improvement_potential: ((data.benchmark - data.actual) / data.actual * 100).toFixed(1) + '%',
          priority: data.performance_ratio < 0.5 ? 'high' : 'medium'
        });
      }
    });
  });

  return optimizations;
}

function analyzeCustomerSegmentPerformance(campaignData) {
  const customerProfile = campaignData.profile || {};

  return {
    journey_stage_performance: {
      stage: customerProfile.journey_stage,
      engagement_score: customerProfile.engagement_score,
      predicted_response_rate: Math.min(1, customerProfile.engagement_score * 1.2),
      segment_characteristics: {
        high_engagement: customerProfile.engagement_score > 0.7,
        at_risk: customerProfile.churn_risk_score > 0.6,
        high_value: customerProfile.predicted_ltv > 1000
      }
    },
    personalization_effectiveness: {
      content_match_score: Math.random() * 0.4 + 0.6, // Mock score
      channel_preference_alignment: Math.random() * 0.3 + 0.7, // Mock score
      timing_optimization_score: Math.random() * 0.2 + 0.8 // Mock score
    }
  };
}

function generatePredictiveRecommendations(patterns, campaignData) {
  const recommendations = [];

  // Based on detected patterns
  patterns.forEach(pattern => {
    if (pattern.confidence > 0.8) {
      recommendations.push({
        prediction_type: 'pattern_based',
        recommendation: `Apply ${pattern.pattern} insights to future campaigns`,
        confidence: pattern.confidence,
        expected_outcome: pattern.recommendation,
        timeframe: '2-4 weeks'
      });
    }
  });

  // Based on customer profile
  const customerProfile = campaignData.profile || {};

  if (customerProfile.churn_risk_score > 0.7) {
    recommendations.push({
      prediction_type: 'churn_prevention',
      recommendation: 'Implement intensive retention campaign within 48 hours',
      confidence: 0.85,
      expected_outcome: '60% reduction in churn probability',
      timeframe: '48 hours'
    });
  }

  if (customerProfile.engagement_score > 0.8) {
    recommendations.push({
      prediction_type: 'expansion_opportunity',
      recommendation: 'Present upsell opportunities with personalized ROI projections',
      confidence: 0.78,
      expected_outcome: '35% increase in upgrade probability',
      timeframe: '1-2 weeks'
    });
  }

  return recommendations;
}

return performanceAnalytics;

