  now.setDate(now.getDate() + 7); // Start in a week
  return now.toISOString().split('T')[0];
}

function calculateEndDate(strategy) {
  const start = new Date(calculateStartDate(strategy));
  const durationWeeks = strategy.duration ? parseInt(strategy.duration.split('-')[0]) : 2;
  start.setDate(start.getDate() + (durationWeeks * 7));
  return start.toISOString().split('T')[0];
}

