function identifyTopPerformers(data) {
  if (!data || data.length === 0) return [];
  return data
    .sort((a, b) => (b.metrics?.efficiency || 0) - (a.metrics?.efficiency || 0))
    .slice(0, 3)
    .map(item => ({ department: item.department, process: item.process }));
}

function identifyImprovementOpportunities(data) {
  if (!data || data.length === 0) return [];
  return data
    .filter(item => (item.metrics?.efficiency || 0) < 0.7)
    .map(item => ({ 
      department: item.department, 
      process: item.process, 
      opportunity: 'efficiency_improvement' 
    }));
}

function identifyBottlenecks(data) {
  if (!data || data.length === 0) return [];
  return data
    .filter(item => (item.metrics?.errors || 0) > 0.1)
    .map(item => ({ 
      department: item.department, 
      process: item.process, 
      issue: 'high_error_rate' 
    }));
}

function identifyBestPractices(data) {
  if (!data || data.length === 0) return [];
  return data
    .filter(item => (item.metrics?.efficiency || 0) > 0.9 && (item.metrics?.quality || 0) > 0.9)
    .map(item => ({ 
      department: item.department, 
      process: item.process, 
      practice: 'high_efficiency_quality' 
    }));
}

